import { Container, Input, Title, Main, Form, Button } from '../../components'
import styles from './styles.module.css'
import { useFormWithValidation } from '../../utils'
import { Redirect } from 'react-router-dom'
import { useContext } from 'react'
import { AuthContext } from '../../contexts'
import MetaTags from 'react-meta-tags'

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    password: '',
    password_re: ''
  });

  const [isValid, setIsValid] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));

    if (formData.password && formData.password_re) {
      setIsValid(formData.password === formData.password_re);
    }
  };

const SignUp = ({ onSignUp }) => {
  const { values, handleChange, errors, isValid, resetForm } = useFormWithValidation()
  const authContext = useContext(AuthContext)

  return <Main>
    {authContext && <Redirect to='/recipes' />}
    <Container>
      <MetaTags>
        <title>Регистрация</title>
        <meta name="description" content="Хакатон - Регистрация" />
        <meta property="og:title" content="Регистрация" />
      </MetaTags>
      <Title title='Регистрация' />
      <Form className={styles.form} onSubmit={e => {
        e.preventDefault()
        onSignUp(values)
      }}>
        <Input
          label='ФИО'
          name='name'
          required
          onChange={handleChange}
        />
        <Input
          label='Логин'
          name='username'
          required
          onChange={handleChange}
        />
        <Input
          label='Пароль'
          type='password'
          name='password'
          required
          onChange={handleChange}
        />
        <Input
          label='Повтор пароля'
          type='password'
          name='password_re'
          required
          onChange={handleChange}
        />
        <Button
          modifier='style_dark-blue'
          type='submit'
          className={styles.button}
          disabled={!isValid}
        >
          Зарегестрироваться
        </Button>
      </Form>
    </Container>
  </Main>
}
}

export default SignUp;
