import { Container, Input, Title, Main, Form, Button } from '../../components'
import styles from './styles.module.css'
import { useFormWithValidation } from '../../utils'
import { AuthContext } from '../../contexts'
import { Redirect } from 'react-router-dom'
import { useContext } from 'react'
import MetaTags from 'react-meta-tags'

const handleSubmit = (e) => {
  e.preventDefault();

  // Проверяем, все ли поля валидны
  if (!isValid) {
    return;
  }

  // Проверяем совпадение паролей
  if (values.password !== values.password_re) {
    setErrorPopup(true);
    return;
  }

  // Формируем данные для отправки
  const data = {
    username: values.username,
    password: values.password,
  };

  // Отправляем данные на сервер
  fetch('http://localhost:3000/api/auth/token/login/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      console.log('Success:', data);
      // Здесь можно обработать успешный ответ
    })
    .catch((error) => {
      console.error('Error:', error);
    });
};


const SignIn = ({ onSignIn }) => {
  const { values, handleChange, errors, isValid, resetForm } = useFormWithValidation()
  const authContext = useContext(AuthContext)

  return <Main>
    {authContext && <Redirect to='/recipes' />}
    <Container>
      <MetaTags>
        <title>Войти на сайт</title>
        <meta name="description" content="Хакатон - Войти на сайт" />
        <meta property="og:title" content="Войти на сайт" />
      </MetaTags>
      <Title title='Войти на сайт' />
      <Form
        className={styles.form}
        onSubmit={e => {
          e.preventDefault()
          onSignIn(values)
        }}
      >
        <Input
          required
          label='Логин'
          name='username'
          onChange={handleChange}
        />
        <Input
          required
          label='Пароль'
          type='password'
          name='password'
          onChange={handleChange}
        />
        <Button
          modifier='style_dark-blue'
          disabled={!isValid}
          type='submit'
          className={styles.button}
        >
          Войти
        </Button>
      </Form>
    </Container>
  </Main>
}

export default SignIn
