import { Container, Input, Title, Main, Form, Button } from '../../components'
import styles from './styles.module.css'
import { useFormWithValidation } from '../../utils'
import { Redirect } from 'react-router-dom'
import { useContext, useState } from 'react'
import { AuthContext } from '../../contexts'
import MetaTags from 'react-meta-tags'

const SignUp = ({ onSignUp }) => {
  const { values, handleChange, isValid, errors } = useFormWithValidation()
  const authContext = useContext(AuthContext)
  const [errorPopup, setErrorPopup] = useState(false)

  const handleSubmit = (e) => {
    e.preventDefault()

    // Проверяем, все ли поля валидны
    if (!isValid) {
      return
    }

    // Проверяем совпадение паролей
    if (values.password !== values.password_re) {
      setErrorPopup(true) // Показываем pop-up, если пароли не совпадают
      return
    }

    // Если пароли совпадают, вызываем функцию регистрации
    onSignUp(values)
  }

  const handleClosePopup = () => {
    setErrorPopup(false) // Закрываем pop-up
  }

  return (
    <Main>
      {authContext && <Redirect to='/recipes' />}
      <Container>
        <MetaTags>
          <title>Регистрация</title>
          <meta name="description" content="Хакатон - Регистрация" />
          <meta property="og:title" content="Регистрация" />
        </MetaTags>
        <Title title='Регистрация' />
        <Form className={styles.form} onSubmit={handleSubmit}>
          <Input
            label='ФИО'
            name='name'
            required
            onChange={handleChange}
            error={errors.name} // Отображаем ошибку, если есть
          />
          <Input
            label='Логин'
            name='username'
            required
            onChange={handleChange}
            error={errors.username} // Отображаем ошибку, если есть
          />
          <Input
            label='Пароль'
            type='password'
            name='password'
            required
            onChange={handleChange}
            error={errors.password} // Отображаем ошибку, если есть
          />
          <Input
            label='Повтор пароля'
            type='password'
            name='password_re'
            required
            onChange={handleChange}
            error={errors.password_re} // Отображаем ошибку, если есть
          />
          <Button
            modifier='style_dark-blue'
            type='submit'
            className={styles.button}
            disabled={!isValid}
          >
            Зарегистрироваться
          </Button>
        </Form>

        {/* Pop-up для ошибки */}
        {errorPopup && (
          <div className={styles.popup}>
            <div className={styles.popupContent}>
              <h2>Ошибка</h2>
              <p>Пароли должны совпадать.</p>
              <Button onClick={handleClosePopup}>OK</Button>
            </div>
          </div>
        )}
      </Container>
    </Main>
  )
}

export default SignUp
